ValidationRule.errorConditionFormula:
/* Bypass validation rule if Record Type is not Enterprise Engagement or they have CoA User perm */
IF(
  NOT( RecordType.DeveloperName = 'Enterprise_Engagement')
  || $Permission.Growth_CoA_User = TRUE,
  FALSE,

  /* Ignore validation rule if stage is not Done - Successful */
  IF(
    NOT(ISPICKVAL(StageName, 'Done - Successful')),
    FALSE,

    /* DSPs must be complete when stage is Done - Successful*/
    NOT(All_DSPs_Confirmed__c)
  )
)
